import React from "react";

// Styles
import "./style.scss";

// Images
import PlaceholderImg from "../../img/placeholder-user.jpg";
import SortIcon from "../../img/sort-icon.png";

export const CustomerTable = ({ customers, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-wrapper">
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th>Username</th>
            <th>Username</th>
            <th>Username</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.length ? (
            customers.map(customer => (
              <tr key={customer.id}>
                <td className="field-avatar">
                  <img
                    src={customer.avatar ? customer.avatar : PlaceholderImg}
                    alt={customer.username}
                  />
                </td>
                <td>{customer.username}</td>
                <td>{customer.username}</td>
                <td>{customer.username}</td>
                <td>{customer.email}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(customer);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(customer)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};













export const OrderTable = ({ orders, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-wrapper">
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th>Customer</th>
            <th>Email</th>
            <th>Product</th>
            <th>Quantity</th>
            <th>Bill</th>
          </tr>
        </thead>
        <tbody>
          {orders.length ? (
            orders.map(order => (
              <tr key={order.id}>
                <td className="field-avatar">
                  <img
                    src={order.image ? order.image : PlaceholderImg}
                    alt={order.name}
                  />
                </td>
                <td>{order.customerName}</td>
                <td>{order.customerEmail}</td>
                <td>{order.productName}</td>
                <td>{order.quantity}</td>
                <td>{order.bill}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(order);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(order)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};
















export const ProductTable = ({ products, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-wrapper">
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th>Name</th>
            <th>Price</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {products.length ? (
            products.map(product => (
              <tr key={product.id}>
                <td className="field-avatar">
                  <img
                    src={product.image ? product.image : PlaceholderImg}
                    alt={product.name}
                  />
                </td>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.description}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(product);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(product)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};
