import axios from "axios";

const apiURL = process.env.REACT_APP_REQRES_API;

function getCustomers() {
  const response = axios.get(`${apiURL}/admin`);
  return response;
}

function getCreatedCustomer({ first_name, last_name, email }) {
  const response = axios.post(`${apiURL}/admin`, {
    email,
    first_name,
    last_name
  });
  console.log(response.data);
  return response;
}

function getUpdatedCustomer(id, user) {
  const response = axios.put(`${apiURL}/admin/${id}`, {
    avatar: user.avatar,
    id: id,
    email: user.email,
    first_name: user.first_name,
    last_name: user.last_name
  });

  return response;
}

function getDeletedCustomer(id) {
  const response = axios.delete(`${apiURL}/admin/${id}`);

  return response;
}

export { getCustomers, getCreatedCustomer, getUpdatedCustomer, getDeletedCustomer };
