import React from "react";

// Styles
import "./style.scss";

// Images
import PlaceholderImg from "../../img/placeholder-user.jpg";
import SortIcon from "../../img/sort-icon.png";

export const CustomerTable = ({ customers, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-responsive">
      <table class="table table-hover">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">First</th>
            <th scope="col">Last</th>
            <th scope="col">Handle</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">1</th>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <th scope="row">2</th>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <th scope="row">3</th>
            <td colspan="2">Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </table>    
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th
              onClick={() => {
                onSortChange("name");
              }}
            >
              <span className="column-sort">
                Username
                <img src={SortIcon} alt="username" />
              </span>
            </th>
            <th
              onClick={() => {
                onSortChange("email");
              }}
            >
              <span className="column-sort">
                Email
                <img src={SortIcon} alt="Email" />
              </span>
            </th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.length ? (
            customers.map(customer => (
              <tr key={customer.id}>
                <td className="field-avatar">
                  <img
                    src={customer.avatar ? customer.avatar : PlaceholderImg}
                    alt={customer.username}
                  />
                </td>
                <td>{customer.username}</td>
                <td>{customer.email}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(customer);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(customer)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};


export const OrderTable = ({ orders, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-wrapper">
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th
              onClick={() => {
                onSortChange("name");
              }}
            >
              <span className="column-sort">
                Username
                <img src={SortIcon} alt="username" />
              </span>
            </th>
            <th
              onClick={() => {
                onSortChange("email");
              }}
            >
              <span className="column-sort">
                Email
                <img src={SortIcon} alt="Email" />
              </span>
            </th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.length ? (
            orders.map(customer => (
              <tr key={customer.id}>
                <td className="field-avatar">
                  <img
                    src={customer.avatar ? customer.avatar : PlaceholderImg}
                    alt={customer.username}
                  />
                </td>
                <td>{customer.username}</td>
                <td>{customer.email}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(customer);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(customer)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};
