import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  getCustomers,
  getCreatedCustomer,
  getUpdatedCustomer,
  getDeletedCustomer
} from "../app/api";

// Components
import DataTable from "../components/DataTable";
import CreateCustomer from "../components/CreateCustomer";
import UpdateCustomer from "../components/UpdateCustomer";
import DeleteCustomer from "../components/DeleteCustomer";
import Modal from "../components/Modal";
import Search from "../components/Search";
import Pagination from "../components/Pagination";
import Loader from "../components/Loader";
import MySwal from "../index";

export const Customer = () => {
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false);  
  const [currentCustomer, setCurrentCustomer] = useState({
    id: null,
    avatar: null,
    first_name: "",
    last_name: "",
    email: ""
  });
  const [customers, setCustomers] = useState([]);
  const [activeModal, setActiveModal] = useState({ name: "", active: false });
  const [pageSize] = useState(5);
  const [currentPage, setCurrentPage] = useState(1);
  const [sorted, setSorted] = useState(false);

  getCustomers()
    .then((response) => {
      setCustomers(response.data);
    })
    .catch(e => {
      console.log(e);
    });

  const customersLastIndex = currentPage * pageSize;
  const customersFirstIndex = customersLastIndex - pageSize;
  const currentCustomers = customers.slice(customersFirstIndex, customersLastIndex);

  // Setting up Modal
  const setModal = modal => {
    search("");
    setActiveModal({ name: modal, active: true });
  };

  // Pagination
  const paginate = page => {
    setCurrentPage(page);
  };

  // Search
  const search = term => {
    if (term.length > 2) {
      setCurrentPage(1);

      const results = customers.filter(customer =>
        Object.keys(customer).some(key =>
          customer[key]
            .toString()
            .toLowerCase()
            .includes(term.toString().toLowerCase())
        )
      );

      dispatch({ type: "SET_CUSTOMERS", data: results });
    } else if (!term.length) {
      dispatch({ type: "SET_CUSTOMERS", data: customers });
    }
  };

  // Sorting
  const sorting = key => {
    setSorted(!sorted);
    switch (key) {
      case "name":
        const nameSort = [...customers].sort((a, b) => {
          return sorted
            ? a.first_name.localeCompare(b.first_name, "tr")
            : b.first_name.localeCompare(a.first_name, "tr");
        });
        dispatch({ type: "SET_CUSTOMERS", data: nameSort });
        return;
      case "surname":
        const surnameSort = [...customers].sort((a, b) => {
          return sorted
            ? a.last_name.localeCompare(b.last_name, "tr")
            : b.last_name.localeCompare(a.last_name, "tr");
        });
        dispatch({ type: "SET_CUSTOMERS", data: surnameSort });
        return;
      case "email":
        const emailSort = [...customers].sort((a, b) => {
          return sorted
            ? a.email.localeCompare(b.email, "tr")
            : b.email.localeCompare(a.email, "tr");
        });
        dispatch({ type: "SET_CUSTOMERS", data: emailSort });
        return;
      default:
        break;
    }
  };

  // Create Customer
  const createCustomer = async customer => {
    setActiveModal(false);
    setLoading(true);

    try {
      await getCreatedCustomer(customer).then(res => {
        const result = res.data;
        MySwal.fire({
          icon: "success",
          title: "Customer created successfully."
        }).then(() => {
          dispatch({ type: "CREATE_CUSTOMER", data: result });
          setCustomers([...customers, result]);
        });
      });
    } catch (err) {
      MySwal.fire({
        icon: "error",
        title: "Failed to create customer."
      });
    } finally {
      setLoading(false);
    }
  };

  // Update Customer
  const updateRow = customer => {
    setModal("Update Customer");

    setCurrentCustomer({
      id: customer.id,
      username: customer.username,      
      email: customer.email,
      password: customer.password
    });
  };

  const updateCustomer = async (id, updatedCustomer) => {
    setActiveModal(false);
    setLoading(true);

    try {
      await getUpdatedCustomer(id, updatedCustomer).then(res => {
        const result = res.data;
        MySwal.fire({
          icon: "success",
          title: "Customer updated successfully."
        }).then(() => {
          dispatch({
            type: "SET_CUSTOMERS",
            data: customers.map(customer =>
              customer.id === id ? Object.assign(customer, result) : customer
            )
          });
        });
      });
    } catch (err) {
      MySwal.fire({
        icon: "error",
        title: "Failed to update customer."
      });
    } finally {
      setLoading(false);
    }
  };

  // Delete Customer
  const deleteRow = customer => {
    setModal("Delete Customer");

    setCurrentCustomer({
      id: customer.id,
      avatar: customer.avatar,
      first_name: customer.first_name,
      last_name: customer.last_name,
      email: customer.email
    });
  };

  const deleteCustomer = async id => {
    setActiveModal(false);
    setLoading(true);

    try {
      await getDeletedCustomer(id).then(() => {
        MySwal.fire({
          icon: "success",
          title: "Customer deleted successfully."
        }).then(() => {
          dispatch({
            type: "SET_CUSTOMERS",
            data: customers.filter(customer => customer.id !== id)
          });
          setCustomers(customers.filter(customer => customer.id !== id));
          setCurrentPage(1);
        });
      });
    } catch (err) {
      MySwal.fire({
        icon: "error",
        title: "Failed to delete customer."
      });
    } finally {
      setLoading(false);
    }
  };

  // Fetch Users
  const fetchUsers = async () => {
    setLoading(true);

    try {
      await getCustomers().then(({ data }) => {
        setCustomers(data.data);
        dispatch({ type: "SET_CUSTOMERS", data: data.data });
      });
    } catch (err) {
      MySwal.fire({
        icon: "error",
        title: "Failed to fetch customers."
      });
    } finally {
      setTimeout(() => {
        setLoading(false);
      }, 500);
    }
  };

  return (
    <div className="app">
      <main className="content">
        <div className="container">
          {loading ? (
            <Loader />
          ) : (
            <div className="content-wrapper">
              <div className="toolbar">
                <Search search={search} resetSearch={search} />
                <button
                  className="primary-btn"
                  onClick={() => setModal("Create Customer")}
                >
                  Create New Customer
                </button>
              </div>
              <DataTable
                customers={currentCustomers}
                updateRow={updateRow}
                deleteRow={deleteRow}
                onSortChange={sorting}
              />
              <Pagination
                totalResults={customers.length}
                currentPage={currentPage}
                pageSize={pageSize}
                paginate={paginate}
              />
            </div>
          )}
        </div>
      </main>
      {activeModal.active && (
        <Modal activeModal={activeModal}>
          {activeModal.name === "Create Customer" && (
            <CreateCustomer
              createCustomer={createCustomer}
              setActiveModal={setActiveModal}
            />
          )}
          {activeModal.name === "Update Customer" && (
            <UpdateCustomer
              currentCustomer={currentCustomer}
              updateCustomer={updateCustomer}
              setActiveModal={setActiveModal}
            />
          )}
          {activeModal.name === "Delete Customer" && (
            <DeleteCustomer
              currentCustomer={currentCustomer}
              deleteCustomer={deleteCustomer}
              setActiveModal={setActiveModal}
            />
          )}
        </Modal>
      )}
    </div>
  );
};
