import axios from "axios";

const apiURL = process.env.REACT_APP_REQRES_API;

function getCustomers() {
  const response = axios.get(`http://localhost:3001admin`);
  return response;
}

function getCreatedCustomer({ first_name, username, email }) {
  const response = axios.post(`${apiURL}/admin`, {
    email,
    first_name,
    username
  });
  console.log(response.data);
  return response;
}

function getUpdatedCustomer(id, customer) {
  const response = axios.put(`${apiURL}/admin/${id}`, {
    id: customer.id,
    username: customer.username,
    email: customer.email,
    password: customer.password
  });
  return response;
}

function getDeletedCustomer(id) {
  const response = axios.delete(`${apiURL}/admin/${id}`);
  return response;
}

export { getCustomers, getCreatedCustomer, getUpdatedCustomer, getDeletedCustomer };
