import axios from "axios";

const apiURL = process.env.REACT_APP_REQRES_API;

function getCustomers() {
  const response = axios.get(`http://localhost:3001/admin`);
  return response;
}

function getCreatedCustomer({ username, email }) {
  const response = axios.post(`http://localhost:3001/admin`, {
    id: 0,
    username,
    email,
    password: '12345678'
  });
  return response;
}

function getUpdatedCustomer(id, customer) {
  const response = axios.put(`http://localhost:3001/admin/${id}`, {
    id: customer.id,
    username: customer.username,
    email: customer.email,
    password: customer.password
  });
  return response;
}

function getDeletedCustomer(id) {
  const response = axios.delete(`http://localhost:3001/admin/${id}`);
  return response;
}

export { getCustomers, getCreatedCustomer, getUpdatedCustomer, getDeletedCustomer };
