import React from "react";

// Styles
import "./style.scss";

// Images
import PlaceholderImg from "../../img/placeholder-user.jpg";
import SortIcon from "../../img/sort-icon.png";

export const CustomerTable = ({ customers, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-wrapper">
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th>Username</th>
            <th>Username</th>
            <th>Username</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.length ? (
            customers.map(customer => (
              <tr key={customer.id}>
                <td className="field-avatar">
                  <img
                    src={customer.avatar ? customer.avatar : PlaceholderImg}
                    alt={customer.username}
                  />
                </td>
                <td>{customer.username}</td>
                <td>{customer.username}</td>
                <td>{customer.username}</td>
                <td>{customer.email}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(customer);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(customer)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};


export const OrderTable = ({ orders, updateRow, deleteRow, onSortChange }) => {
  return (
    <div className="table-wrapper">
      <table className="data-table">
        <thead>
          <tr>
            <th></th>
            <th
              onClick={() => {
                onSortChange("name");
              }}
            >
              <span className="column-sort">
                Username
                <img src={SortIcon} alt="username" />
              </span>
            </th>
            <th
              onClick={() => {
                onSortChange("email");
              }}
            >
              <span className="column-sort">
                Email
                <img src={SortIcon} alt="Email" />
              </span>
            </th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.length ? (
            orders.map(customer => (
              <tr key={customer.id}>
                <td className="field-avatar">
                  <img
                    src={customer.avatar ? customer.avatar : PlaceholderImg}
                    alt={customer.username}
                  />
                </td>
                <td>{customer.username}</td>
                <td>{customer.email}</td>
                <td className="field-actions">
                  <button
                    className="primary-btn"
                    onClick={() => {
                      updateRow(customer);
                    }}
                  >
                    Update
                  </button>
                  <button
                    className="field-actions__delete"
                    onClick={() => deleteRow(customer)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">
                <div className="no-record-message">No Record!</div>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};
