import React, { useState } from "react";

// Styles
import "./app.scss";
import "bootstrap/dist/css/bootstrap.css";


// Components
import { Container } from 'react-bootstrap';
import Header from "./components/Header";
import Footer from "./components/Footer";
import { Customer } from "./section/Customer";
import { Tab } from "./components/Header/Tab";
import { Order } from "./section/Order";

function App() {
  const [selected, setSelected] = useState('Products');
  return (
    <div className = "app">
      <Container>
        <Header tabs = {['Products', 'Orders', 'Customers', 'Suppliers']} selected = {selected} setSelected = {(tab) => setSelected(tab)}>
          <Tab isSelected = {selected === 'Products'}>
            <Order />
          </Tab>
          <Tab isSelected = {selected === 'Orders'}>
            Orders
          </Tab>
          <Tab isSelected = {selected === 'Customers'}>
            <Customer />
          </Tab>
          <Tab isSelected = {selected === 'Suppliers'}>
            Suppliers
          </Tab>
        </Header>
      </Container>
      <Footer />
    </div>
  );
}

export default App;
